import React, { useEffect, useState, ChangeEvent } from 'react';
import axios from 'axios';

type Todo = {
  id: number;
  title: string;
};

function App() {
  const [todos, setTodos] = useState<Todo[]>([]);
  const [newTodo, setNewTodo] = useState<string>('');

  useEffect(() => {
    axios.get<Todo[]>('http://localhost:5000/todos')
      .then(res => setTodos(res.data));
  }, []);

  const addTodo = (): void => {
    if (!newTodo.trim()) return;
    axios.post<Todo>('http://localhost:5000/todos', { title: newTodo })
      .then(res => setTodos([...todos, res.data]));
    setNewTodo('');
  };

  const deleteTodo = (id: number): void => {
    axios.delete(`http://localhost:5000/todos/${id}`)
      .then(() => setTodos(todos.filter((todo: Todo) => todo.id !== id)));
  };

  const handleInputChange = (e: ChangeEvent<HTMLInputElement>): void => {
    setNewTodo(e.target.value);
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.title}>‚úÖ Tarefas Conclu√≠das</h1>
      <div style={styles.inputContainer}>
        <input
          value={newTodo}
          onChange={handleInputChange}
          placeholder="Digite uma nova tarefa..."
          style={styles.input}
        />
        <button onClick={addTodo} style={styles.addButton}>Adicionar</button>
      </div>

      <ul style={styles.list}>
        {todos.map((todo: Todo) => (
          <li key={todo.id} style={styles.listItem}>
            <span>{todo.title}</span>
            <button onClick={() => deleteTodo(todo.id)} style={styles.deleteButton}>üóëÔ∏è</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

const styles = {
  container: {
    maxWidth: 600,
    margin: '40px auto',
    padding: 20,
    backgroundColor: '#121212',
    borderRadius: 12,
    boxShadow: '0 4px 12px rgba(0, 0, 0, 0.3)',
    fontFamily: 'Arial, sans-serif',
    color: '#eee'
  },
  title: {
    textAlign: 'center' as const,
    color: '#ffffff'
  },
  inputContainer: {
    display: 'flex',
    marginBottom: 20,
    gap: 10
  },
  input: {
    flex: 1,
    padding: 10,
    border: '1px solid #444',
    borderRadius: 8,
    fontSize: 16,
    backgroundColor: '#1e1e1e',
    color: '#fff'
  },
  addButton: {
    padding: '10px 20px',
    backgroundColor: '#4CAF50',
    color: 'white',
    border: 'none',
    borderRadius: 8,
    cursor: 'pointer'
  },
  list: {
    listStyle: 'none',
    padding: 0
  },
  listItem: {
    backgroundColor: '#1e1e1e',
    padding: '10px 15px',
    marginBottom: 10,
    borderRadius: 8,
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    border: '1px solid #333',
    color: '#ccc'
  },
  deleteButton: {
    backgroundColor: '#ff4d4f',
    color: 'white',
    border: 'none',
    borderRadius: 6,
    padding: '5px 10px',
    cursor: 'pointer'
  }
};

export default App;
